function main(){adacta.info("Create batches...");createBatches();adacta.info("Done!")}function createBatches(){scanBatches.forEach(createBatch)}function createBatch(b){var a=adacta.getRootIndex();var e=b.scanEmployee+"_"+b.scanWaNr+"_"+b.scanSeqNr;var d=adacta.makeFolders(a,e);var c=[];c["ada:scanEmployee"]=b.scanEmployee;c["ada:scanSeqNr"]=b.scanSeqNr;c["ada:scanWaNr"]=b.scanWaNr;c["ada:scanBatchName"]=e;createBatchFiles(d,5,c)}function createBatchFiles(e,d,c){for(var a=0;a<d;a++){var b="example_"+Math.floor(Math.random()*1000000)+".pdf";var f=adacta.importClasspathFile(null,e,b);c["ada:docDateCreated"]=new Date();c["ada:scanBatchSize"]=d;f.addAspect("ada:scanAspect",c);adacta.log("Importing batch file "+b+".")}}var scanBatches=[{scanEmployee:"u000001",scanSeqNr:"1001",scanWaNr:"WA1000001"},{scanEmployee:"u000002",scanSeqNr:"1002",scanWaNr:"WA1000002"},{scanEmployee:"u000004",scanSeqNr:"1004",scanWaNr:"WA1000004"}];main();